public int length(String str){
	if (str.isEmpty()) {
		return 0;
	} else {
		return 1 + length(str.substring(1));
	}
}

public int sum(int[] arr){
	int x = 0;
	for (int i=0; i<arr.length; i++) {
		x = arr[i];
	}
	return x;
}

public int sum(int[] arr, int idx, int accum){
	if (arr.length == idx) {
		return accum;
	} else {
		return sum(arr, idx+1, accum+arr[idx]);
	}
}

